from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right = Motor(Port.B, Direction.CLOCKWISE)
front = Motor(Port.E, Direction.COUNTERCLOCKWISE)
top = Motor(Port.F, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 62.4, 101)

async def main():
    print(prime_hub.battery.voltage())
    drive_base.use_gyro(True)
    front.control.limits(torque=1000)
    drive_base.settings(straight_speed=1000)
    await drive_base.straight(-10)
    await multitask(
        drive_base.straight(695),
        front.run_angle(100, -97),
    )
    await drive_base.turn(-45)
    drive_base.settings(straight_speed=200)
    await drive_base.straight(175)
    await front.run_angle(200, 95)
    await drive_base.straight(-180)
    await top.run_angle(200, -90)
    await drive_base.straight(120)
    await top.run_angle(200, 130)
    drive_base.settings(straight_speed=1000)
    await drive_base.straight(-30)
    await drive_base.turn(-125)
    await drive_base.straight(550)


run_task(main())