from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right = Motor(Port.B, Direction.CLOCKWISE)
front = Motor(Port.E, Direction.COUNTERCLOCKWISE)
top = Motor(Port.F, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 62.4, 101)

async def main():
    drive_base.use_gyro(True)
    top.control.limits(torque=1000)
    drive_base.settings(straight_speed=500)
    await drive_base.straight(-790)
    await drive_base.straight(30)
    await front.run_angle(200, -100)
    await drive_base.turn(-90)
    drive_base.settings(straight_speed=200)
    await drive_base.straight(80)
    await top.run_angle(1000, 40)
    await drive_base.straight(60)
    await wait(500)
    await top.run_angle(500, -55)
    await front.run_angle(200, 120)
    await front.run_angle(200, -130)
